# Brute-force + bit manipulation + subset enumeration (1000+ms 300+mb)
```cpp
class Solution {
public:
    vector<int> findNumOfValidWords(vector<string>& words, vector<string>& puzzles) {
        vector<int> count(1<<26, 0);
        for (int i = 0; i < words.size(); i++) {
            int bit = 0;
            for (auto c: words[i]) {
                bit |= (1<<(c-'a'));
            }
            count[bit] ++;
        }
        vector<int> res;
        for (int i = 0; i < puzzles.size(); i++) {
            int cnt = 0;
            for (int j = 0; j < (1<<6); j++) {
                int bit = 1<<(puzzles[i][0]-'a');
                for (int k = 0; k < 6; k++) {
                    if ((j & (1<<k)) > 0) {
                        bit |= 1<<(puzzles[i][k+1]-'a');
                    }
                }
                cnt += count[bit];
            }
            res.push_back(cnt);
        }
        return res;
    }
};
```

# Same idea as the above solution, in a smarter way. (150+ms, 30mb)
- use hashmap instead of vector of size 2^26.
- subset enumeration by `i = (i-1)&mask` 
```cpp
class Solution {
    int getMask(string & s) {
        int mask = 0;
        for (auto c: s)
            mask |= (1<<(c-'a'));
        return mask;
    }
public:
    vector<int> findNumOfValidWords(vector<string>& words, vector<string>& puzzles) {
        unordered_map<int, int> freq;
        for (auto & w: words) {
            int mask = getMask(w);
            freq[mask] ++;
        }
        vector<int> res;
        for (auto & p : puzzles) {
            int mask = getMask(p);
            int cnt = 0;
            int maskFirstChar = 1<<(p[0]-'a');
            for (int i = mask; i > 0; i = ((i-1) & mask)) {
                if ((i & maskFirstChar) > 0 && freq.count(i)) {
                    cnt += freq[i];
                }
            }
            res.push_back(cnt);
        }
        return res;
    }
};
```

# Trie of words, search by puzzles (160+ms 60+mb)
```cpp
class Solution {
    struct TrieNode{
        int countEnd;
        TrieNode* children[26];
        TrieNode() {
            countEnd = 0;
            memset(children, 0, sizeof(TreeNode*)*26);
        }
    };
    TrieNode trie;

    void insert(int countChar[26]) {
        TrieNode * currNode = & trie;
        for (int i = 0; i < 26; i++) {
            if (countChar[i] == 1) {
                if (currNode->children[i] == nullptr)
                    currNode->children[i] = new TrieNode();
                currNode = currNode->children[i];
            }
        }
        currNode->countEnd ++;
    }

    void buildTrie(vector<string> & words) {
        int countChar[26];
        for (int i = 0; i < words.size(); i++) {
            memset(countChar, 0, sizeof(int)*26);
            string & w = words[i];
            for (auto c: w) { countChar[c-'a'] = 1; }
            int count = 0;
            for (int i = 0; i < 26; i++) { count += countChar[i]; }
            if (count > 7) continue;
            insert(countChar);
        }
    }

	int search(TrieNode* node, string & p, int idx, char firstChar, bool metFirst) {
        int res = 0;
        if (node->countEnd > 0 && metFirst) {
            res += node->countEnd;
        }
        for (int i = idx; i < p.length(); i++) {
            if (node->children[p[i]-'a'] != nullptr) {
                res += search(node->children[p[i]-'a'], p, i+1, firstChar, metFirst || (p[i] == firstChar));
            }
        }
        return res;
    }
public:
    vector<int> findNumOfValidWords(vector<string>& words, vector<string>& puzzles) {
        buildTrie(words);
        vector<int> res;
        for (int i = 0; i < puzzles.size(); i++) {
            char firstChar = puzzles[i][0];
            sort(puzzles[i].begin(), puzzles[i].end());
            int cnt = search(&trie, puzzles[i], 0, firstChar, false);
            res.push_back(cnt);
        }
        return res;
    }
};
```

# Two-fold hashtable + bit manipulation(400+ms, 40+mb)
```cpp
class Solution {
public:
    vector<int> findNumOfValidWords(vector<string>& words, vector<string>& puzzles) {
        unordered_map<char, unordered_map<int, vector<int>>> mem;
        for (auto & s : words) {
            int key = 0;
            for (auto & c : s) {
                key |= (1<<(c-'a'));
            }
            int first13 = key & (((1<<13)-1)<<13);
            for (int j = 0; j < 26; j++) {
                if ((key & (1<<j)) > 0 ) {
                    mem['a'+j][first13].push_back(key);
                }
            }
        }
        vector<int> res(puzzles.size(), 0);
        for (int i = 0; i < puzzles.size(); i++) {
            string & s = puzzles[i];
            int key = 0;
            for (auto c : s) {
                key |= (1<<(c-'a'));
            }
            int first13 = key & (((1<<13)-1)<<13);
            char firstChar = s[0];
            if (mem.count(firstChar)) {
                for (auto & kv1 : mem[firstChar]) {
                    if ((first13 & kv1.first) == kv1.first) {
                        for (auto n : kv1.second) {
                            if ((key & n) == n) {
                                    res[i] ++;
                            }
                        }
                    }
                }
            } 
        }
        return res;
    }
};
```

