        for (int i=0;i<word.length();i++){
            int j = word.charAt(i)-'a';
            if (cur.children[j]==null){
                cur.children[j] = new Trie();
            }
            cur = cur.children[j];
        }
        cur.word = word;
        cur.count++;
    }



    class Trie{
        Trie[] children = new Trie[26];
        String word = null;
        int count = 0;
    }
}
