class Solution {
public:
    vector<int> findNumOfValidWords(vector<string>& words, vector<string>& puzzles) {
        unordered_map<char,vector<int>> map; //to store mask of each char in the word

        for(string word:words){
            int mask = 0; //calculate mask
            for(char ch:word){
                int bit = ch-'a';
                mask = mask | (1<<bit);
            }
            set<char> unique;  /* to store only unique values, here we are storing the mask in every char that starts with ch, we are precalculating 
			it to avoid it calculating the mask for every character every time (if we do this then we are repeating some tasks, hence pre-store 
			the mask for all words that starts with a char in puzzle*/

            for(char ch : word){
                if(unique.find(ch)!=unique.end())continue;
                unique.insert(ch);
                map[ch].push_back(mask);
            }
        }

        vector<int> res;
        for(string puzzle:puzzles){
            int pmask = 0;
            for(char ch:puzzle){
                int bit = ch-'a';
                pmask = pmask | (1<<bit);
            }
            char firstchar = puzzle[0];
            int count = 0;
            vector<int> wordsToCheck = map[firstchar]; //fetch all the word masks that have first char in puzzle
            for(int wmask : wordsToCheck){
                if((wmask & pmask) == wmask)count++; //if all the chars of word are present in puzzle then & of wmask and pmask will be equal to mask of word, i.e wmask.
            }
            res.push_back(count);
        }
        return res;
    }
};
