public class Solution {
    public IList<int> FindNumOfValidWords(string[] words, string[] puzzles) {        
        // Add words to the Trie
        var trie = new Trie();          
        foreach (var word in words) {
            // Get distinct characters in the word and sort it
            var chars = word.Distinct().ToArray();

            // Longer words are never valid
            if (chars.Length <= 7) {
                Array.Sort(chars);
                trie.Insert(new String(chars));
            }
        }

        // Check how many words in the puzzle match in the Trie
        var result = new List<int>();
        foreach (var word in puzzles) {
            result.Add(trie.Search(word));
        }        
        return result;
    }  

    // Trie
    public class Trie {
        private TrieNode root;

        public Trie() {
            root = new TrieNode();
        }
        public void Insert(string word) {
            var node = root;
            foreach (var ch in word) {
                if (!node.ContainsKey(ch)) {
                    node.Add(ch, new TrieNode());
                }
                node = node.Get(ch);
            }
            node.SetComplete();
        }
        public int Search(string word) {
            return DFS(root, word, false);
        }        
        private int DFS(TrieNode node, string word, bool firstFound) {
            var result = firstFound ? node.WordCount() : 0;
            foreach (var ch in word) {
                if (node.ContainsKey(ch)) {
                    result += DFS(node.Get(ch), word, firstFound || ch == word[0]);
                }
            }
            return result;
        }
    }

    // TrieNode
    class TrieNode {
        private Dictionary<char, TrieNode> children;
        private bool isComplete;
        private int count;

        public TrieNode() {
            children = new Dictionary<char, TrieNode>();
        }
        public bool ContainsKey(char ch) {
            return children.ContainsKey(ch);
        }
        public TrieNode Get(char ch) {
            return children[ch];
        }
        public void Add(char ch, TrieNode node) {
            children[ch] = node;
        }
        public void SetComplete() {
            isComplete = true;
            ++count;
        }
        public bool IsComplete() {
            return isComplete;
        }
        public int WordCount() {
            return count;
        }
    }   
}
