class Solution {
public:
    vector<int> findNumOfValidWords(vector<string>& words, vector<string>& puzzles) {
        /*
        map ->
        a== mask of( aaaa , able , ability , actt,actor,access)
        b== mask of( able , ability)
        c== so on

        using set to not pushing mask again if char repeats like aaaa , a is repeating.

        again make mask of every puzzle word.

        take the mask vector of the first char of puzzleword.

        check if the puzzle also contains the word by using & operator.

         */
        vector<int> ans;

        unordered_map<char,vector<int>> m;
        //Map to store map of every word from vector words.

        for(string word : words){
            //calculating mask of every word
            int mask =0;
            for(char ch : word){
                int bit = ch - 'a';
                mask = mask | (1<<bit);

            }

            set <char> unique;
            //Here we are using set to not add the mask again if the word will come again.
            for(char ch : word){
                if(unique.count(ch))continue;
                //pushing the mask into the map.
                m[ch].push_back(mask);
                unique.insert(ch);
            }
        }


        for(string puzzle : puzzles){

            //calculate mask of the puzzle
            int pmask =0;
            for(char ch : puzzle){
                int bit = ch - 'a';
                pmask = pmask | (1<<bit);

            }
            //take the vector of mask contains first character.
            vector<int>  wordstocheck = m[puzzle[0]];
            int count =0;
            for(int wmask : wordstocheck){

                //check if puzzle mask and word mask contains char.
                if((wmask & pmask) == wmask){
                   count++; 
                }
            }

            ans.push_back(count);

        }

        return ans;
    }
};
