};

public:
    vector<int> findNumOfValidWords(vector<string>& words, vector<string>& puzzles) {

      struct TrieNode* root = getNode();

      /* 
      Insert each word[i] in Trie,Then for each puzzles[i] search in Trie

      There is no need to insert duplicate chars of word, also order of char in a word do not matter , so insert each word in trie in sorted order without any duplicate

      To do this , first insert each chars of a word in a set 
      */

      for(string& s:words){
        set<char>st;  //using set to avoid duplicates as well as word also get sorted

        st.insert(s.begin(),s.end());

        string sorted_str="";
        for(char ch:st)
          sorted_str+=ch;

        insert(root,sorted_str);

    }

