        }
        // parsing puzzles into bitmasks
        for (int i = 0, tmp, start; i < pLen; i++) {
            s = puzzles[i];
            // turning s into a bitmask
            tmp = 0;
            for (char c: s) {
                c -= 'a';
                tmp |= 1 << c;
            }
            // storing the bitmask in bits by its starting letter
            start = s[0] - 'a';
            for (int j = 0, lmtJ = bitsPos[start]; j < lmtJ; j++) {
                if ((bits[start][j] & tmp) == bits[start][j]) res[i]++;
            }
        }
        return res;
    }
};
```

