var findNumOfValidWords = function(words, puzzles) {

    let wordCountMap = new Map();
    let res = [];

    for(let i = 0; i < words.length; i++){

        let mask = buildbitmask(words[i]);

        if(wordCountMap.has(mask)){
            wordCountMap.set(mask, wordCountMap.get(mask) + 1);
        }else{
            wordCountMap.set(mask, 1);
        }
    }

    //console.log(wordCountMap)

    for(let i = 0; i < puzzles.length; i++){

        let first = 1 << (puzzles[i].charCodeAt(0) - 97);
        let count = wordCountMap.has(first)? wordCountMap.get(first):0;


        let mask = buildbitmask(puzzles[i].substring(1));

        for(let submask = mask; submask > 0; submask = (submask - 1) & mask){

            count += wordCountMap.has(submask | first) ? wordCountMap.get(submask | first) : 0;
        }

        res.push(count);
    }


    return res;
};

const buildbitmask = function(word) {

    let mask = 0;
    for(let i = 0; i < word.length; i++){

        mask |= 1 << (word.charCodeAt(i) - 97);
    }
    return mask;
}

