```typescript
function findNumOfValidWords(words: string[], puzzles: string[]): number[] {
  const A: number = 'a'.charCodeAt(0);

  const bit = (char: string): number => {
    return (1 << (char.charCodeAt(0) - A));
  }

  const signature = (word: string): number => {
    let sign: number = 0;
    for (const c of word) sign |= bit(c);
    return sign;
  }

  const signatureCounts: Map<number, number> = new Map();
  for (const word of words) {
    const sign = signature(word);
    const count = signatureCounts.get(sign) || 0;
    signatureCounts.set(sign, count + 1);
  }

  return puzzles.map((puzzle): number => {
    const first = bit(puzzle[0]);
    const puzzleSign = signature(puzzle.slice(1));
    let count = signatureCounts.get(first) || 0;
    for (let submask = puzzleSign; submask > 0; 
        submask = (submask - 1) & puzzleSign) {
      count += (signatureCounts.get(submask | first) || 0); 
    }
    return count;
  });
};
```

