vector<int> findNumOfValidWords(vector<string>& words, vector<string>& puzzles) {
        vector<int> ans;
        unordered_map<char,vector<string>> startsWith;
        unordered_map<string,vector<bool>> charMap;
        for(string word:words){
            vector<bool> store(26,false);
            for(char x:word)
                store[x-'a']=true;
            for(int i=0;i<26;i++){
                if(store[i])
                    startsWith[char('a'+i)].push_back(word);
            }
            charMap[word]=store;
        }

        for(string puzzle:puzzles){
            int count=0;
            vector<bool> store(26,false);
            for(char x:puzzle) store[x-'a']=true;
            for(string possible:startsWith[puzzle[0]]){
                vector<bool> check=charMap[possible];
                bool cond=true;
                for(int i=0;i<26;i++){
                    if(check[i] and !store[i]) cond=false;
                }
                if(cond) count++;
            }
            ans.push_back(count);
        }

        return ans;
    }
