vector<int> findNumOfValidWords(vector<string>& words, vector<string>& puzzles) {
        vector<int> ans;
        unordered_map<char,vector<int>> startsWith;
        for(string word:words){
            int mask=0;
            for(char x:word)
                mask|=(1<<(x-'a'));
            for(int i=0;i<26;i++){
                if(mask&(1<<i))
                    startsWith[char('a'+i)].push_back(mask);
            }
        }
        for(string puzzle:puzzles){
            int count=0;
            int smask=0;
            for(char x:puzzle)
                smask|=(1<<(x-'a'));
            for(int mask:startsWith[puzzle[0]]){
                if((smask&mask)==mask)
                    count++;
            }
            ans.push_back(count);
        }
        return ans;
    }
