class Solution {
public:
	vector<int> findNumOfValidWords(vector<string>& words, vector<string>& puzzles) {
		map<int, vector<int>>hashMap;
		int n = words.size();
		int m = puzzles.size();
		vector<int>ans(m, 0);     // to store final answer
		for (int i = 0; i < n; i++) {
			int word_mask = 0;
			// generating mask for each word
			for (auto v : words[i])
				word_mask = (word_mask | (1 << (v - 'a')));

			// to restrict pushing the mask of a same word again with the same key
			// For eg: aaaa will be pushed four times in hashmap if not kept track
			vector<bool>check(26, 0);

			for (auto c : words[i]) {
				int r = c - 'a';
				if (check[r] == 1)
					continue;
				hashMap[c - 'a'].push_back(word_mask);
				check[r] = 1;
			}
		}

		for (int i = 0; i < m; i++) {
			// generating mask for each puzzle
			int puzzle_mask = 0;
			for (auto v : puzzles[i]) {
				puzzle_mask = (puzzle_mask | (1 << (v - 'a')));
			}
			int count = 0;
			// iterating over words mask which contains first character of puzzle
			for (auto word_mask : hashMap[puzzles[i][0] - 'a']) {
			// if the '&' and of both mask is equal to the words mask increment count
				if ((word_mask & puzzle_mask) == word_mask)
					count++;
			}
			ans[i] = count;
		}

		return ans;
	}
};
