object Solution {
  def findNumOfValidWords(words: Array[String], puzzles: Array[String]): List[Int] = {
    val encodedWords = words.map(encodeWord)
    puzzles
      .toList
      .map(Puzzle(_))
      .map(puzzle => encodedWords.count(puzzle.isValid))
  }

  private def encodeChar(char: Char) = 1 << (char - 'a')

  private def encodeWord(word: String) = word.map(encodeChar).reduce(_ | _)

  private final case class Puzzle(first: Char, encoding: Int) {
    def isValid(encodedWord: Int): Boolean = (encodedWord & encodeChar(first)) != 0 && (encodedWord & ~encoding) == 0
  }

  private object Puzzle {
    def apply(puzzle: String): Puzzle = Puzzle(puzzle.head, encodeWord(puzzle))
  }
}
