class Solution {
public:

    // Two ways are there to solve this question
    // 1. Take every element of the puzzle, get the map of all the characters in it and simultaneously check for the those characters in list words
    // 2. Store the mask obtained from the list words in unordered_map. Take each string from the list puzzles, prepare the temporary mask for all the possible combination and check for that particular mask in unordered_map.


    // Since According to the given constraint as the puzzles[i] length is fixed=7. We can easily choose the 2nd option. As this is going to reduce the time and space complexity.
    vector<int> findNumOfValidWords(vector<string>& words, vector<string>& puzzles) {
        unordered_map<int,int>mp;
        int Mod=pow(2,6);          // Combination start point. As the first element must be in the word list.
        for(int i=0;i<words.size();i++){
            int mask=0;
            for(int j=0;j<words[i].length();j++){
                mask|=(1<<(words[i][j]-'a'));
            }
            mp[mask]++;                                 // Adding the prepared mask in the unordered_map
        }
        vector<int>ans(puzzles.size(),0);
        for(int i=0;i<puzzles.size();i++){
            int count=0;
            for(int j=Mod;j<(1<<7);j++){                // Going for all the possible combination in the list puzzles 
                int tmask=0;  
                for(int k=0;k<7;k++){
                    if((j>>k)&1){
                        int index=7-k-1;
                        tmask|=(1<<(puzzles[i][index]-'a'));     // preparing the temporary mask;
                    }
                }
                if(mp.find(tmask)!=mp.end()){
                    count+=mp[tmask];
                }
            }
            ans[i]=count;
        }
        return ans;
    }
};

