class Solution {
public:
    vector<int> findNumOfValidWords(vector<string>& words, vector<string>& puzzles) {
        unordered_map<int,int> mp;
        for(auto w:words){
            int mask=0;
            for(auto a:w){
                mask|=(1<<(a-'a'));
            }
            mp[mask]++;
        }
        vector<int> ans;
        for(auto p:puzzles){
            int su=0;
            for(auto a:p){
                su|=(1<<(a-'a'));
            }
            int mas=su;
