class Solution {
    public List<Integer> findNumOfValidWords(String[] words, String[] puzzles) {
        List<Integer>res=new ArrayList<>();
        Map<Integer,Integer>map=new HashMap<>();
        for(String w:words){
            int code=encode(w);
            if(map.containsKey(code)){
                map.put(code,map.get(code)+1);
            }else{
                map.put(code,1);
            }
        }

        for(int i=0;i<puzzles.length;i++){
            int sum=0;
            String puzzle=puzzles[i];
            Set<Integer>set=new HashSet<>();
            List<Integer>list=new ArrayList<>();
            for(int k=0;k<puzzle.length();k++){
                set.add((puzzle.charAt(k)-'a'));
            }
            generate(new ArrayList<>(set),0,0,list);
            for(int n:list){
                if(map.containsKey(n)){
                    int first=puzzle.charAt(0)-'a';
                    first=1<<first;
                    if((n&first)!=0){
                        sum+=map.get(n);
                    }
                }
            }
            res.add(sum);
        }
        return res;
    }

    public void generate(List<Integer>nums,int index,int n,List<Integer>list){
        if(index>=nums.size()){
            list.add(n);
            return;
        }
        int pos=nums.get(index);
        int copyn1=n;
        int copyn2=n;
        copyn1=copyn1|(1<<pos);
        generate(nums,index+1,copyn1,list);
        generate(nums,index+1,copyn2,list);
    }

    public int encode(String s){
        int res=0;
        for(int i=0;i<s.length();i++){
            int pos=s.charAt(i)-'a';
            res=res|(1<<pos);
        }
        return res;
    }
}
