  object Solution {

    def findNumOfValidWords(words: Array[String], puzzles: Array[String]): List[Int] = {

      import scala.collection.mutable.Map
      class Node(_v: Char) {
        val v = _v
        val children = Map[Char, Node]()
        var word = ""
      }

      val countMap = Map[String, Int]()
      words.foreach(w => countMap.put(w, countMap.getOrElse(w, 0) + 1))

      def f(root: Node, set: Set[Char], fc: Char): Int = {
        var counter = 0

        def g(root: Node, set: Set[Char], fc: Char, hasMet: Boolean): Unit = {
          if (root.v != ' ' && !set.contains(root.v)) return
          val newHasMet = if (root.v == fc) true else hasMet
          if (root.word.length > 0 && newHasMet) counter += countMap(root.word)
          for (c <- root.children.keySet) g(root.children(c), set, fc, newHasMet)
        }

        g(root, set, fc, false)
        counter
      }

      val root = new Node(' ')
      words.foreach(w => {
        var runner = root
        for (i <- 0 until w.length) {
          if (!runner.children.contains(w.charAt(i))) runner.children.put(w.charAt(i), new Node(w.charAt(i)))
          runner = runner.children(w.charAt(i))
          if (i == w.length - 1) runner.word = w
        }
      })
      puzzles.map { p => f(root, p.toCharArray.toSet, p charAt 0) }.toList
    }
  }

