class Solution {
    public List<Integer> findNumOfValidWords(String[] words, String[] puzzles) {
        HashMap<Integer, Integer> counter = new HashMap<>();
        for(String s: words){
            int temp = 0;
            for(char c: s.toCharArray())
                temp |= (1 << (c - 'a'));
            counter.put(temp, counter.getOrDefault(temp, 0) + 1);
        }
        List<Integer> res = new ArrayList<Integer>();
        for(String s : puzzles)
            res.add(backtrack(s, counter, 1, 1 << (s.charAt(0) - 'a')));
        return res;
    }

    public int backtrack(String s, HashMap<Integer, Integer> counter, int level, int path){
        if(level == 7) return counter.getOrDefault(path, 0);
        return backtrack(s, counter, level + 1, path | 1 << (s.charAt(level) - 'a')) + backtrack(s,counter, level + 1, path);
    }
}

