class TrieNode:
    def __init__(self, s=''):
        self.s = s
        self.n = 0
        self.child = {}
class Solution:
    def findNumOfValidWords(self, words: [str], puzzles: [str]) -> [int]:
        wl = map(lambda  i : sorted(set(i)) , words )
        t = TrieNode()
        for w in wl:
            t1 = t
            for c in w:
                if c not in t1.child:
                    t1.child[c] = TrieNode(c)
                t1 = t1.child[c]
            t1.n +=1
        ans = []
        for pz in puzzles:
            a = 0
            st = list( [i, 1 if i.s ==pz[0] else 0] for i in  t.child.values() if i.s in pz )
            while len(st)>0:
                p = st.pop()
                if p[0].s in pz:
                    st += list( [i, 1 if i.s ==pz[0] else p[1]] for i in  p[0].child.values() if i.s in pz)
                    if p[1] ==1 :
                        a += p[0].n
            ans.append(a)
        return ans
