```C++
typedef long long ll;
class Solution {
public:
    vector<ll> puzzleset;
    map<ll, ll> wordset;
    ll d[26];
    int getones(ll n){
        int bits = 0;
        while(n!=0){
            if(n&1 == 1) bits++;
            n = n >> 1;
        }
        return bits;
    }
    ll getnum(string &s, int n){
        ll sum = 0;
        int len = s.length();
        int i = 0;
        while(n!=0){
            if(n&1 == 1){
                sum |= d[s[len-i-1]-'a'];
            }
            i++;
            n = n>>1;
        }
        return sum;
    }
    vector<int> findNumOfValidWords(vector<string>& words, vector<string>& puzzles) {
        vector<int> result;
        for(int i = 0; i < 26; i++){
            d[i] = (1<<i);
        }
        for(int i = 0; i < words.size(); i++){
            ll sum = 0;
            for(int j = 0; j < words[i].length(); j++){
                sum |= d[words[i][j]-'a'];
            }
            if(getones(sum) <= 7){
                if(wordset.find(sum) == wordset.end()) wordset[sum] = 1;
                else  wordset[sum]++;
            }

        }
         for(int i = 0; i < puzzles.size(); i++){
            ll sum = 0;
            for(int j = 0; j < puzzles[i].length(); j++){
                sum |= d[puzzles[i][j]-'a'];
            }

            puzzleset.push_back(sum);
        }


        for(int i = 0; i < puzzleset.size(); i++){
            int sum = 0;
            string str = puzzles[i];
            // list different situation on 6 bits
            for(int j = 0; j < (1<<6); j++){
                ll x = getnum(str, j) | d[str[0]-'a'];
                if(wordset.find(x) != wordset.end()) sum+=wordset[x];
            }
            result.push_back(sum);
        }
        return result;
    }
};
