            for (int subPat : subPatterns)
                cnt += wordPattern.getOrDefault(subPat, 0);
            ans.add(cnt);
        }
        return ans;
    }

    private ArrayList<Integer> getAllSubPatterns(String s) {
        ArrayList<Integer> ans = new ArrayList<>();

        for (int i = 0; i < (1 << 6); i++) {
            StringBuilder sb = new StringBuilder();
            sb.append(s.charAt(0));
            for (int k = 0; k < 6; k++)
                if ((i & masks[k]) != 0)
                    sb.append(s.charAt(k + 1));

            ans.add(getPattern(sb.toString()));
        }
        return ans;
    }

    private int getPattern(String s) {
        boolean[] contain = new boolean[26];
        for (char c : s.toCharArray()) contain[c - 'a'] = true;
        int ans = 0;
        int cnt = 0;
        for (int i = 0; i < 26; i++) {
            if (contain[i]) {
                cnt++;
                ans |= masks[i];
            }
        }
        if (cnt <= 7) return ans;
        return -1;
    }
}
