class Solution {
public:
    vector<int> findNumOfValidWords(vector<string>& words, vector<string>& puzzles) {
        unordered_map<int, int> wordMasks; 
        vector<int> ans; 
        for (const string& word : words) {
            int mask = 0;
            for (char c : word) {
                mask |= (1 << (c - 'a'));
            }
            wordMasks[mask]++;
        }

        for (const string& puzzle : puzzles) {
            int puzzleMask = 0;
            for (char c : puzzle) {
                puzzleMask |= (1 << (c - 'a'));
            }

            int firstCharMask = (1 << (puzzle[0] - 'a'));
            int subsetMask = puzzleMask;

            int totalValidWords = 0;
            while (subsetMask > 0) {
                if ((subsetMask & firstCharMask) && wordMasks.count(subsetMask)) {
                    totalValidWords += wordMasks[subsetMask];
                }
                subsetMask = (subsetMask - 1) & puzzleMask; 
            }

            ans.push_back(totalValidWords);
        }

        return ans;
    }
};

```

