object Solution {
    def findNumOfValidWords(words: Array[String], puzzles: Array[String]): List[Int] = {
      val wordMap = words.foldLeft(scala.collection.immutable.Map.empty[Int, Int].withDefaultValue(0)){(map, word) =>
        val bitmask = word.foldLeft(0)((bitmask, char)=>bitmask | (1 << (char - 'a')))
        map.updated(bitmask, map(bitmask) + 1)
      }

      def generateSubsets(puzzle: String, index: Int, bitmask: Int): Int =
        if (index == puzzle.length) wordMap(bitmask)
        else generateSubsets(puzzle, index + 1, bitmask | (1 << (puzzle(index) - 'a'))) + generateSubsets(puzzle, index + 1, bitmask)

      puzzles.map(puzzle => generateSubsets(puzzle, 1, 1 << (puzzle(0) - 'a'))).toList
    }
}
