class Trie{
    public:
    Node* root;
    Trie()
    {
      root=new Node();
    }
    void insert(int num)
    {
        Node* node=root;
        for (int i=25;i>=0;i--)
        {
            int bit=((num>>i)&1);
            if (!node->isContains(bit))
            {
                node->put(bit,new Node());
            }
            node=node->get(bit);
            node->incCount();
        }
    }
    int getCount(Node* root)
    {
        if (root==NULL) return 0;
        return root->count();
    }
    int rec(Node* root,int cur,int ind,int val)
    {
        if (root==NULL) return 0;
        if (ind==-1) return getCount(root);
        int bit=((cur>>ind)&1);
        if (val==ind)
        {
            return rec(root->get(1),cur,ind-1,val);
        }
        else if (bit==1)
        {
            return rec(root->get(1),cur,ind-1,val)+rec(root->get(0),cur,ind-1,val);
        }   
        else return rec (root->get(0),cur,ind-1,val);     
    }
};
