class Solution {
public:
    int dfs(unordered_map<char, unordered_map<string, int>>& m,
            string& cur, int i, string& s, char& key){
        if (i == s.size()) return 0;
        cur += s[i];
        int ans = 0;
        if (m[key].find(cur) != m[key].end()) ans += m[key][cur];
        ans += dfs(m, cur, i + 1, s, key);
        cur.pop_back();
        ans += dfs(m, cur, i + 1, s, key);
        return ans;
    }
    // 1) for each word
    // insert its hashmap into all the possible characters of its hashmap
    // 2) for each puzzle
    // search for each subsequence that starts with its key
    vector<int> findNumOfValidWords(vector<string>& words, vector<string>& puzzles) {
        unordered_map<char, unordered_map<string, int>> m;
        vector<int> ans;
        for (auto& s : words){
            vector<bool> freq(26);
            for (auto& c : s) freq[c - 'a'] = true;
            string key = "";
            for (int i = 0; i < freq.size(); ++i)
                if (freq[i]) key += i + 'a';
            for (int i = 0; i < freq.size(); ++i)
                if (freq[i]) ++m[i + 'a'][key];
        }
        for (auto& s : puzzles){
            char key = s[0];
            sort(s.begin(), s.end());          
            string cur = "";
            ans.push_back(dfs(m, cur, 0, s, key));
        }
        return ans;
    }
};
