class Solution {
    public List<Integer> findNumOfValidWords(String[] words, String[] puzzles) {
        List<Integer> ans = new ArrayList<>();
        HashMap<Integer , Integer> maskToCount = new HashMap<>();
        HashMap<Character , HashSet<Integer>> map = new HashMap<>();
        for(int i = 0 ; i < 26 ; i++){
            map.put((char)(i+'a') , new HashSet<>());
        }
        for(int i = 0 ; i < words.length ; i++){
            int mask = 0;
            for(int j = 0 ; j < words[i].length() ; j++){
                char ele = words[i].charAt(j);
                int tempMask = (1 << (int)(ele - 'a'));
                mask = mask | tempMask;
            }
            maskToCount.put(mask , maskToCount.getOrDefault(mask , 0) + 1);
            for(int j = 0 ; j < words[i].length() ; j++){
                char c = words[i].charAt(j);
                map.get(c).add(mask);
            }
        }
        for(int i = 0 ; i < puzzles.length ; i++){
            int curMask = 0;
            for(int j = 0 ; j < puzzles[i].length() ; j++){
                char ele = puzzles[i].charAt(j);
                curMask = curMask | (1 << (int)(ele - 'a'));
            }
            int count = 0;
            for(int ele : map.get(puzzles[i].charAt(0))){
                if((ele & curMask) == ele) {
                    count += maskToCount.getOrDefault(ele , 0);
                }
            }
            ans.add(count);
        }
        return ans;
    }
}
