class Solution {
public:

    void solve(int i,long long curr, long long mask,unordered_map<long long,int>& temp,long long& count,int first){

        if(i>25) return  ;

        bool isSet = (mask&(1<<i));

        solve(i+1,curr,mask,temp,count,first);

        if(isSet && (i!=first) ) {

            curr|=(1<<i);

            count+= temp[curr];

            solve(i+1,curr,mask,temp,count,first);

        }


