        for(auto word:words){

            long long mask = 0;

            for(auto ch:word) mask|=(1<<(ch-'a'));

            freq[mask]++; 

        }

        for(auto word:puzzles){

            // set bits can be zero or one 
            long long mask = 0 ,count =0 ,curr=0;

            for(auto ch:word) mask|=(1<<(ch-'a'));

            curr|=(1<<(word[0]-'a'));

            count+= freq[curr];

            solve(0,curr,mask,freq,count,word[0]-'a');

            ans.push_back(count);

        }

        return ans; 

    }
};
