class Solution {
    public List<Integer> findNumOfValidWords(String[] words, String[] puzzles) {
        int n = words.length;
        int m = puzzles.length;
        List<Integer> result = new ArrayList<>();

        // map of bit vs count of numbers which has that bit as set
        Map<Integer, Map<Integer, Integer>> bitMap = new HashMap<>();
        for(String s: words) {
            int map[] = new int[26];
            for(char c: s.toCharArray()) {
                map[c-'a'] = 1;              
            }

            int number = 0;
            for(int i = 0; i<26; i++) {
                if(map[i] > 0)
                    number = (number | (1<<i));
            }

            for(int i = 0; i<26; i++) {
                if(map[i] > 0) {
                    bitMap.putIfAbsent(i, new HashMap<>());
                    bitMap.get(i).put(number, bitMap.get(i).getOrDefault(number, 0) + 1);
                }
            }
        }

        // valid word for each puzzle would be one among the set bit combinations
        // of the number represented by the word.
        for(int i = 0; i<m; i++) {
            int currResult = 0;
            List<Integer> numberCombinations = new ArrayList<>();
            solve(0, puzzles[i], 0, numberCombinations);
            int firstLetterBit = puzzles[i].charAt(0) - 'a';
            Map<Integer, Integer> numbers = bitMap.get(firstLetterBit);

            if(numbers == null) {
                result.add(currResult);
                continue;
            }

            for(int number: numberCombinations)
                currResult += numbers.getOrDefault(number, 0);

            result.add(currResult);
        }

        return result;
    }

    // O(2^7)
    void solve(int idx, String word, int number, List<Integer> result) {
        if(idx >= word.length()) {
            if(number > 0)
                result.add(number);

            return;
        }

        // dont set the bit by the current index
        solve(idx + 1, word, number, result);
        // Set the bit by the current index
        int bit = word.charAt(idx) - 'a';
        solve(idx + 1, word, number | (1<<bit), result);
        return;
    }
}
