struct Trie{
    Trie* links[26];
    int end;
    Trie(){
        for(int i=0;i<26;i++) links[i]=NULL;
        end=0;
    }
};

void insert(Trie* root,string &word){
    int n=word.length();
    for(auto &ch:word){
        int idx=ch-'a';
        if(root->links[idx]==NULL){
            root->links[idx] = new Trie();
        }
        root = root->links[idx];
    }
    root->end++;
}
char first_ch;
int search(Trie *root, string &word,bool seen){
    int count=0;
    if(seen) count+=root->end;
    for(auto &ch:word){
        int idx = ch-'a';
        if(ch==first_ch and root->links[idx]!=NULL){
            // cout<<first_ch<<endl;
            count += search(root->links[idx],word,true);
        }
        else if(root->links[idx]!=NULL){
            count += search(root->links[idx],word,seen);
        }
    }
    return count;
}


class Solution {
public:
    vector<int> findNumOfValidWords(vector<string>& words, vector<string>& puzzles) {
        Trie* root = new Trie(); 
        for(auto &w:words){
            unordered_set<int> s(w.begin(),w.end());
            string tmp="";
            for(auto &it:s) tmp.push_back(it);
            insert(root,tmp);
        }
        vector<int> res;
        for(auto &p:puzzles){
            first_ch = p[0];
            int ans = search(root,p,false);
            res.push_back(ans);
        }
        return res;
    }
};
