class Solution:
    def getWordMask(self, word: str) -> int:
        bitmask = 0
        for char in word:
            bitmask |= 1 << ord(char) - ord('a')
        return bitmask

    def findNumOfValidWords(self, words: List[str], puzzles: List[str]) -> List[int]:
        maskFreq, res = defaultdict(int), []
        for word in words:
            maskFreq[self.getWordMask(word)] += 1
        for puzzle in puzzles:
            mask = self.getWordMask(puzzle)
            submask, first, curr = mask, 1 << ord(puzzle[0]) - ord('a'), 0
            while submask:
                if submask & first:
                    curr += maskFreq[submask]
                submask = (submask - 1) & mask
            res.append(curr)
        return res
