  let count = [];
  let wordsLetterMap = new Array(words.length).fill(0);

  for(let i = 0; i < words.length; i++){
      for (letter of words[i]){
          let index = letter.charCodeAt(0)-97;
          wordsLetterMap[i] |= (1 << (26-index)); 
