    let currentCount = 0;
    for(let j = 0; j < words.length; j++){
        let index = puzzles[i].charCodeAt(0)-97;
        if((wordsLetterMap[j] & (1 << (26-index))) && 
        ((wordsLetterMap[j] & puzzlesLetterMap[i]) == wordsLetterMap[j])){
            currentCount++;
        }
