		for j := range w[i] { m |= 1 << (w[i][j] - 97) }
		bw[m] += 1
		for j := range w[i] { ch[(w[i][j] - 97)][m] = struct{}{} }
	}

	r := make([]int, len(p))
	for i := range p {
		pm := 0
		for j := range p[i] { pm |= 1 << (p[i][j] - 97) }
		c := 0
		for wm := range ch[p[i][0] - 97] {
			if wm&pm == wm { c += bw[wm] }
		}
		r[i] = c
	}
	return r
}
