class trie:
    def __init__(self):
        self.child=defaultdict(trie)
        self.count=0
class Solution:
    def findNumOfValidWords(self, words: List[str], puzzles: List[str]) -> List[int]:
        root=trie()
        def build(s):
            r=root
            for i in s:
                r=r.child[i]
            r.count+=1
        for i in words:
            build("".join(sorted(set(i))))
        def f(r,s,first,i):
            if i==len(s):
                return r.count
            ans=f(r.child[s[i]],s,first,i+1)
            if s[i]!=first:
                ans+=f(r,s,first,i+1)
            return ans
        return [f(root,"".join(sorted(set(i))),i[0],0) for i in puzzles]
