func findNumOfValidWords(words []string, puzzles []string) []int {
	hash := make(map[int]int)
	for _, word := range words {
		bitmask := 0
		for _, c := range word {
			bitmask |= 0x1<<(c-'a')
		}
		hash[bitmask]++
	}
	result := make([]int, len(puzzles))
	for resIndex, puzzle := range puzzles {
		base := 0x1<<(puzzle[0]-'a')
		footprint := make([]bool, 26)
		footprint[puzzle[0]-'a'] = true
		extend := make([]int, 0, 6)
		for i := 1; i < len(puzzle); i++ {
			if footprint[puzzle[i]-'a'] == true {
				continue
			}
			footprint[puzzle[i]-'a'] = true
			extend = append(extend, int(puzzle[i]-'a'))
		}
		result[resIndex] = dfs(hash, base, extend, 0)
	}
	return result
}

func dfs(hash map[int]int, currBitmask int, extend []int, index int) int {
	if index == len(extend) {
		return hash[currBitmask]
	}
	value := 0
	value += dfs(hash, currBitmask, extend, index+1)
	value += dfs(hash, currBitmask|(0x1<<extend[index]), extend, index+1)
	return value
}
