    				count += map.get(sub); // add freq of the word

    			sub = (sub-1) & puzzlemask; //get to th next substring
    		}
    		ans.add(count);
    	}
    	return ans;
    }

	private  int createMask(String str)
	{
		int mask = 0;
		for(int i=0;i<str.length();i++)
		{
			int bit = str.charAt(i)-'a';
			mask = (mask | (1 << bit));
		}
		return mask;
	}
