class Solution {
    public List<Integer> findNumOfValidWords(String[] words, String[] puzzles) {
        List<Integer> result = new ArrayList<>();
        if (puzzles == null || puzzles.length == 0) {
            return result;
        } else if (words == null || words.length == 0) {
            for (int i = 0; i < puzzles.length; i++) {
                result.add(0);
            }
            return result;
        }

        int[] puzzleBits = new int[puzzles.length];
        Map<Integer, Integer> wordBitCnt = new HashMap<>();
        for (int i = 0; i < puzzles.length; i++) {
            puzzleBits[i] = stringToBit(puzzles[i]);
        }
        for (int i = 0; i < words.length; i++) {
            int wordBit = stringToBit(words[i]);
            wordBitCnt.put(wordBit, wordBitCnt.getOrDefault(wordBit, 0) + 1);
        }

        for (int i = 0; i < puzzles.length; i++) {
            int count = 0;
            for (int wordBit : wordBitCnt.keySet()) {
                if ((wordBit & (1 << (puzzles[i].charAt(0) - 'a'))) == 0) {
                    continue;
                }
                if (((~puzzleBits[i]) & wordBit) == 0) {
                    count += wordBitCnt.get(wordBit);
                }
            }
            result.add(count);
        }

        return result;
    }

    private int stringToBit(String s) {
        int result = 0;
        for (char c : s.toCharArray()) {
            result |= (1 << (c - 'a'));
        }
        return result;
    }
}


