var ans []int
type trieNode struct {
	cnt int
	next map[byte]*trieNode
}

type Trie struct {
	_tr trieNode
}

func TrieConstructor() Trie {
	return Trie{trieNode{0, make(map[byte]*trieNode)}}
}

func (this *Trie) Insert(word string)  {
	if word == "" {return}

	p := &this._tr
	for i := 0; i < len(word); i++ {
		if p.next[word[i]] == nil {
			p.next[word[i]] = &trieNode{0, make(map[byte]*trieNode)}
		}

		p = p.next[word[i]]
	}

	p.cnt ++
}

func (this *Trie) _Search(puzzle map[byte]struct{}, p *trieNode, head bool, idx int, fc byte) {
	if p.cnt > 0 && head {ans[idx] += p.cnt}

	for k, v := range p.next {
		if _, exist := puzzle[k]; exist {
			this._Search(puzzle, v, head || k == fc, idx, fc)
		}
	}
}

func (this *Trie) Search(puzzle string, idx int) {
	p := &this._tr
	m := make(map[byte]struct{})
	for i := 0; i < len(puzzle); i++ {m[puzzle[i]] = struct{}{}}

	this._Search(m, p, false, idx, puzzle[0])
}

func findNumOfValidWords(words []string, puzzles []string) []int {
	ans = make([]int, len(puzzles))

	tr := TrieConstructor()
	for i := 0; i < len(words); i++ {
		ss := []byte(words[i])

		sort.Slice(ss, func(i, j int) bool {
			return ss[i] < ss[j]
		})

		b := make([]byte, 0, len(ss))
		b = append(b, ss[0])
		for j := 1; j < len(ss); j++ {
			if ss[j] == ss[j - 1] {continue}
			b = append(b, ss[j])
		}
		if len(b) > 7 {continue}

		tr.Insert(string(b))
	}

	for i := 0; i < len(puzzles); i++ {
		tr.Search(puzzles[i], i)
	}

	return ans
}
