private static final int N_ABC = 26;

public List<Integer> findNumOfValidWords(String[] words, String[] puzzles) {

	long[] mWords = map(words, false);
	long[] mPuzzles = map(puzzles, true);

	List<Integer> r = new LinkedList<>();
	for (long puzzle: mPuzzles) {
		int k = 0;
		for (long word: mWords) {
			if((puzzle & word) == word &&
			   ((puzzle >>> N_ABC) & word) == (puzzle >>> N_ABC)) k++;           
		}
		r.add(k);
	}    
	return r;
}

private long[] map (String[] a, boolean isFirst) {
	long[] mask = new long[a.length];
	for (int i = 0; i < a.length; i++) {
		if(isFirst) mask[i] |= (1L << (a[i].charAt(0) - 'a' + N_ABC));
		for (int j = 0; j < a[i].length(); j++) {
			mask[i] |= (1L << a[i].charAt(j) - 'a');
		}
	}
	return mask;
}
