        for(String puzzle:puzzles){
            int pmask=createMask(puzzle);
            int c=0;
            for(int wmask:map.get(puzzle.charAt(0))){
                if((pmask & wmask ) == wmask) c++;
            }
            res.add(c);
        }
        return res;
