const A_CHAR_CODE = 97;

/**
 * @param {string[]} words
 * @param {string[]} puzzles
 * @return {number[]}
 */
var findNumOfValidWords = function(words, puzzles) {
    const wordCount = new Map();

    for (word of words) {
        const mask = bitmask(word);

        if (wordCount.has(mask)) {
            wordCount.set(mask, wordCount.get(mask) + 1);
        } else {
            wordCount.set(mask, 1);
        }
    }

    const result = [];

    for (const puzzle of puzzles) {
        const first = 1 << (puzzle.charCodeAt(0) - A_CHAR_CODE);
        let count = wordCount.get(first) || 0;
        const mask = bitmask(puzzle.substring(1));
        let submask = mask;

        while (submask > 0) {
            count += wordCount.get(submask | first) || 0;
            submask = (submask - 1) & mask;
        }

        result.push(count);
    }

    return result;
};

function bitmask(word) {
    let mask = 0;

    for (let i = 0; i < word.length; i++) {
        mask |= (1 << (word.charCodeAt(i) - A_CHAR_CODE));
    }

    return mask;
}
