class Solution {
public:
    int createBitMask(string &s) {
        int mask = 0;

        for(int i=0; i<s.size(); i++) {
            int bit = s[i]-'a';
            mask = mask | (1<<bit);
        }

        return mask;
    }

    // vector<int> subsets;
    vector<int> result;
    unordered_map<int,int> wordsMaskHashMap;
    int index = 0;
    void createSubsets(int mask, int firstLetterBit, int currentMask, int bit) {
        if(bit==26) {
            // subsets.push_back(currentMask);

            result[index] += wordsMaskHashMap[currentMask];

            return;
        }

        if( (mask&(1<<bit)) == 0) {
            createSubsets(mask, firstLetterBit, currentMask, bit+1);
        }
        else {
            int nextMask;

            //set it once
            nextMask = currentMask | (1<<bit);
            createSubsets(mask, firstLetterBit, nextMask, bit+1);

            //unset it once
            if(bit!=firstLetterBit) {
                nextMask = currentMask;
                createSubsets(mask, firstLetterBit, nextMask, bit+1);
            }
        }
    }

    void printMask(int mask) {
        while(mask>0) {
            cout<<(mask&1);
            mask = mask>>1;
