object Solution {
    val maskWord = (word : String) => word
                                        .map(c => 1 << c - 'a')
                                        .reduce(_ | _)

    def findNumOfValidWords(words: Array[String], puzzles: Array[String]): List[Int] = {
        val wordMask = words
                        .map(maskWord)

        val ans = puzzles.map(puzzle => {
            val puzzleMask = maskWord(puzzle)
            val firstPuzzleCharMask = 1 << (puzzle(0) - 'a')
            wordMask
            .count(word => {
                (word & firstPuzzleCharMask)==firstPuzzleCharMask && 
                ((puzzleMask & word) == word)
            })
        })

        ans.toList
    }
}

