class Solution {
    public List<Integer> findNumOfValidWords(String[] words, String[] puzzles) {
        Map<Character,ArrayList<Integer>> map = new HashMap<>();
        List<Integer> res = new ArrayList<>();

        // Step 1 : Create bucket for all the 26 characters
        for(int i = 0;i<26;i++){
            map.put((char)('a' + i),new ArrayList<>());
        }

        // Step 2 : Mask each word and store the bitmask correspondig to all letters that the word has.
        for(String word : words){
            int mask = 0;
            for(char ch : word.toCharArray()){
                int bit = ch - 'a';
                mask = mask | (1 << bit);
            }
            Set<Character> set = new HashSet<>();
            for(char ch : word.toCharArray()){
                if(set.contains(ch))continue;
                set.add(ch);
                map.get(ch).add(mask);
            }
        }

        // Step 3 : For each of the puzzle , get the arraylist from map for that puzzle and check if ((bitmask(puzzle) & bitmask(word)) == bitmask(word))
        for(String puzzle : puzzles){
            int mask = 0;
            for(char ch : puzzle.toCharArray()){
                int bit = ch - 'a';
                mask = mask | (1 << bit);
            }

            List<Integer> wordsToCheck = map.get(puzzle.charAt(0));
            int count = 0;
            for(int temp : wordsToCheck){
                if((mask & temp) == temp){
                    count++;
                }
            }

            res.add(count);
        }

        return res;
    }
}
