public class DictNode {
    public Dictionary<char,DictNode> Dict = new Dictionary<char,DictNode>();
    public int Count = 0;
}

public class Solution {
    public IList<int> FindNumOfValidWords(string[] words, string[] puzzles) {
        List<int> counts = new List<int>();

        DictNode root = new DictNode();
        foreach (string word in words) {
            List<char> chs = word.ToCharArray().ToList();
            chs.Sort();
            char prev = '\0';
            DictNode prevNode = root;
            for (int i = 0; i < chs.Count; i++) {
                if (chs[i] != prev) {
                    prev = chs[i];

                    if (!prevNode.Dict.ContainsKey(chs[i])) {
                        prevNode.Dict.Add(chs[i], new DictNode());
                    }

                    prevNode = prevNode.Dict[chs[i]];
                }

                if (i == chs.Count - 1) {
                    prevNode.Count++;
                }
            }
        }

        foreach (string puzzle in puzzles) {
            counts.Add(DrillDown(root, puzzle, false));
        }

        return counts;
    }

    private int DrillDown(DictNode node, string puzzle, bool firstCharPresent) {
        int total = firstCharPresent ? node.Count : 0;
        foreach (KeyValuePair<char, DictNode> kvp in node.Dict) {
            if (puzzle.Contains(kvp.Key)) {
                if (puzzle[0] == kvp.Key) {
                    total += DrillDown(kvp.Value, puzzle, true);
                } else {
                    total += DrillDown(kvp.Value, puzzle, firstCharPresent);
                }
            }
        }

        return total;
    }
}
