class Solution {
public:
    // Encoding all the puzzles and checking
        //   00000000000000001111000000 -> Puzzle 
        // 1 00000000110000000011000000 -> incorrect 
        // 2 00000000000000001001000000 -> correct
        // 3 00000000000000000000001100 -> incorrect -> a&b works 

        // Two conditions -> first one is puzzle is superset of words, second is first char must be there. 

        /* 
        Solution: 
            1. encoding all the 7 letter puzzle string into binary string -> "1100000000000000000" for ab 
            2. storing each encoded string with a first letter -> in a pair 
                vector<pair<char, string>> encode for the puzzle 
            3. encode the words also 
            4. if(word[encode[i].first - 'a'] == 0) return false
            5. else fill all puzzle ones in the word 
            6. if puzzle == word -> count[i]++
        */
    string Encode(string s){
        string ans = "";
        for(int i =0; i<26; i++)
            ans += "0";

        for(int i = 0; i<s.length(); i++){
            ans[s[i] - 'a'] = '1';
        }
        return ans;
    }

    vector<int> findNumOfValidWords(vector<string>& words, vector<string>& puzzles) {

        vector<pair<char, string>> encodeP;
        vector<string> encodeW;
        vector<int> ans(puzzles.size(), 0);

        int i = 0;

        // Make Encoding 
        while(i < puzzles.size() || i < words.size()){
            if(i < puzzles.size())
                encodeP.push_back(make_pair(puzzles[i][0], Encode(puzzles[i])));
            if(i < words.size())
                encodeW.push_back(Encode(words[i]));
            i++;
        }

        for(int j = 0; j<puzzles.size(); j++){
            int count = 0;
            string tempP = encodeP[j].second;
            char f = encodeP[j].first;
            vector<int> chars(7, 0);

            for(int ii = 0; ii<7; ii++){
                chars[ii] = puzzles[j][ii] - 'a';
            }

            for(int k = 0; k < words.size(); k++){
                string tempW = encodeW[k];
                if(tempW[f - 'a'] == '0')
                    continue;
                for(int jj = 0; jj<7; jj++){
                    tempW[chars[jj]] = '1';
                }
                if(tempW == tempP)
                    count++;
            }

            ans[j] = count;
        }

        return ans;
    }
};

