 int cal(string word){

        int mask=0;

        for(auto x: word){
            mask|=(1<<(x -'a'));
        }

        return mask;
    }
    vector<int> findNumOfValidWords(vector<string>& words, vector<string>& puzzles) {

        map<int,int> mp;
        vector<int> ans;
        for(auto x: words){
            mp[cal(x)]++;
        }

       for(auto x: puzzles){

           int mask=cal(x);
           int submask=mask;
           int an=0;
           int first=(1<<(x[0]-'a'));

           while(submask){

               if(submask&first){
                   an+=mp[submask];
               }
               submask=(submask - 1)&mask;
           }
           ans.push_back(an);
       }

        return ans;
    }
