class Solution {
public:
    string intToRoman(int num) {
        string ans="";
        int val=1;
        while(num>0){
            string ch="";
            if(num>=1000)
                val=1000;
            else if(num>=900)
                val=900;
            else if(num>=500)
                val=500;
            else if(num>=400)
                val=400;
            else if(num>=100)
                val=100;
            else if(num>=90)
                val=90;
            else if(num>=50)
                val=50;
            else if(num>=40)
                val=40;
            else if(num>=10)
                val=10;
            else if(num==9)
                val=9;
            else if(num>=5)
                val=5;
            else if(num==4)
                val=4;
            else if(num>=1)
                val=1;

            switch(val){
                case 1: ch="I";break;
                case 5: ch="V";break;
                case 10: ch="X";break;
                case 50: ch="L";break;
