        for (int pos = 0; pos < triangle.get(row).size(); pos++){
            dp1[pos] = triangle.get(row).get(pos) + Math.min(dp[pos+1], dp[pos]);
        }
        dp = dp1;
    }

    return dp[0];
}
}

```

